const RARITY_TABLE = [{"key": "Legendary", "weight": 0.002}, {"key": "Epic", "weight": 0.01}, {"key": "Rare", "weight": 0.09}, {"key": "Common", "weight": 0.898}];


// Final project script - includes stories, rarity, stats, dropdown switching, face texture mapping
const MODELS_PATH = 'assets/models/';
const modelSelect = document.getElementById('modelSelect');
const faceUpload = document.getElementById('faceUpload');
const watchAd = document.getElementById('watchAd');
const generateBtn = document.getElementById('generate');
const saveBtn = document.getElementById('save');
const downloadBtn = document.getElementById('download');
const hint = document.getElementById('hint');
const nameEl = document.getElementById('charName');
const rarityEl = document.getElementById('rarity');
const rarityBadge = document.getElementById('rarityBadge');
const powerEl = document.getElementById('power');
const statsEl = document.getElementById('stats');
const storyEl = document.getElementById('story');
const galleryEl = document.getElementById('gallery');

let scene, camera, renderer, controls, currentModel=null, facePlane=null, audioBuffer=null, audioCtx=null;

initThree();
function initThree(){ const container = document.getElementById('viewer'); scene = new THREE.Scene(); scene.background = new THREE.Color(0x041428); camera = new THREE.PerspectiveCamera(50, container.clientWidth/container.clientHeight, 0.1, 1000); camera.position.set(0,1.6,3); renderer = new THREE.WebGLRenderer({antialias:true,alpha:true,preserveDrawingBuffer:true}); renderer.setSize(container.clientWidth, container.clientHeight); renderer.setPixelRatio(Math.min(window.devicePixelRatio||1,2)); container.innerHTML=''; container.appendChild(renderer.domElement); const hemi = new THREE.HemisphereLight(0xffffff,0x222233,0.6); scene.add(hemi); const key = new THREE.DirectionalLight(0xffffff,0.9); key.position.set(5,8,5); scene.add(key); controls = new THREE.OrbitControls(camera, renderer.domElement); controls.enableDamping=true; window.addEventListener('resize', onResize); animate(); loadModel(modelSelect.value); }

function onResize(){ const container = document.getElementById('viewer'); camera.aspect = container.clientWidth/container.clientHeight; camera.updateProjectionMatrix(); renderer.setSize(container.clientWidth, container.clientHeight); }

function animate(){ requestAnimationFrame(animate); if(currentModel) currentModel.rotation.y += 0.003; controls.update(); renderer.render(scene, camera); }

const loader = new THREE.GLTFLoader();
function loadModel(filename){ const url = MODELS_PATH + filename; if(currentModel){ scene.remove(currentModel); currentModel.traverse(n=>{ if(n.geometry) n.geometry.dispose && n.geometry.dispose(); if(n.material) { if(Array.isArray(n.material)) n.material.forEach(m=>m.dispose && m.dispose()); else n.material.dispose && n.material.dispose(); } }); currentModel = null; } loader.load(url, gltf=>{ currentModel = gltf.scene; const box = new THREE.Box3().setFromObject(currentModel); const size = box.getSize(new THREE.Vector3()); const maxDim = Math.max(size.x,size.y,size.z); const scale = 1.6 / (maxDim||1); currentModel.scale.set(scale,scale,scale); box.setFromObject(currentModel); const center = box.getCenter(new THREE.Vector3()); currentModel.position.sub(center); scene.add(currentModel); addFacePlane(box); }, xhr=>{}, err=>{ console.error(err); alert('فشل تحميل الموديل'); }); }

function addFacePlane(box){ if(facePlane){ scene.remove(facePlane); facePlane = null; } const min = box.min, max = box.max; const width = max.x - min.x, height = max.y - min.y; const planeW = Math.max(0.25, width*0.35); const planeH = Math.max(0.25, height*0.28); const frontZ = max.z + 0.01; const yPos = min.y + height*0.63; facePlane = new THREE.Mesh(new THREE.PlaneGeometry(planeW, planeH), new THREE.MeshBasicMaterial({transparent:true})); facePlane.position.set(0, yPos, frontZ); facePlane.renderOrder = 999; scene.add(facePlane); facePlane.visible = false; }

modelSelect.addEventListener('change', ()=>{ loadModel(modelSelect.value); });

faceUpload.addEventListener('change', e=>{ const f = e.target.files[0]; if(!f) return; const reader = new FileReader(); reader.onload = ()=>{ const img = new Image(); img.onload = ()=>{ const tex = new THREE.Texture(img); tex.needsUpdate = true; if(facePlane){ facePlane.material.map = tex; facePlane.material.needsUpdate = true; facePlane.visible = true; showHint('تم تطبيق الصورة كوجه'); } else showHint('لا يوجد مكان للوجه على هذا الموديل'); }; img.src = reader.result; }; reader.readAsDataURL(f); });

watchAd.addEventListener('click', ()=>{ watchAd.disabled = true; let t=5; watchAd.textContent = '⏳ ' + t; const iv = setInterval(()=>{ t--; watchAd.textContent = '⏳ ' + t; if(t<=0){ clearInterval(iv); watchAd.textContent = '🎥 شاهد إعلان'; generateBtn.disabled = false; showHint('تم فتح التوليد'); } },1000); });

function pickRarity(){ const r=Math.random(); let cum=0; for(const it of RARITY_TABLE){ cum+=it.weight; if(r<=cum) return it.key; } return 'Common'; }
function rand(arr){ return arr[Math.floor(Math.random()*arr.length)]; }
function randInt(a,b){ return Math.floor(Math.random()*(b-a+1))+a; }

generateBtn.addEventListener('click', ()=>{ const rarity = pickRarity(); const name = rand(NAMES) + ' #' + Math.floor(Math.random()*90000+1000); const power = rand(POWERS); const story = rand(STORIES); const stats = {}; STATS_KEYS.forEach(k=> stats[k]=randInt(8,95)); if(rarity==='Rare'){ for(const k in stats) stats[k]=Math.min(100,stats[k]+10); } if(rarity==='Epic'){ for(const k in stats) stats[k]=Math.min(100,stats[k]+22); } if(rarity==='Legendary'){ for(const k in stats) stats[k]=Math.min(100,stats[k]+40); } nameEl.textContent = name; rarityEl.textContent = rarity; rarityBadge.textContent = rarity; powerEl.textContent = power; storyEl.textContent = story; statsEl.innerHTML = ''; STATS_KEYS.forEach(k=>{ const d = document.createElement('div'); d.className='stat'; d.innerHTML = '<strong>'+k+'</strong><div>'+stats[k]+'</div>'; statsEl.appendChild(d); }); saveBtn.disabled = false; downloadBtn.disabled = false; const gallery = JSON.parse(localStorage.getItem('models_gallery')||'[]'); gallery.unshift({id:Date.now(), name, rarity, power, stats, story, model: modelSelect.value}); localStorage.setItem('models_gallery', JSON.stringify(gallery.slice(0,50))); renderGallery(); showHint('تم إنشاء الشخصية'); });

saveBtn.addEventListener('click', ()=>{ showHint('تم الحفظ في المعرض (localStorage)'); });

downloadBtn.addEventListener('click', ()=>{ try{ const data = renderer.domElement.toDataURL('image/png'); const a = document.createElement('a'); a.href = data; a.download = 'character.png'; a.click(); }catch(e){ alert('تعذر تنزيل الصورة'); } });

function renderGallery(){ const gallery = JSON.parse(localStorage.getItem('models_gallery')||'[]'); galleryEl.innerHTML = ''; gallery.forEach(item=>{ const div = document.createElement('div'); div.className='item'; div.innerHTML = '<strong>'+item.name+'</strong><div>'+item.rarity+' • '+item.power+'</div><button>عرض</button>'; const btn = div.querySelector('button'); btn.onclick = ()=>{ showEntry(item); }; galleryEl.appendChild(div); }); }
function showEntry(it){ nameEl.textContent = it.name; rarityEl.textContent = it.rarity; powerEl.textContent = it.power; storyEl.textContent = it.story; statsEl.innerHTML = ''; for(const k in it.stats){ const d = document.createElement('div'); d.className='stat'; d.innerHTML = '<strong>'+k+'</strong><div>'+it.stats[k]+'</div>'; statsEl.appendChild(d); } }

function showHint(t){ hint.textContent = t; setTimeout(()=>{ if(hint.textContent === t) hint.textContent = 'اضغط \"شاهد إعلان\" لتمكين التوليد.'; },2500); }

renderGallery();
